import 'package:flutter/material.dart';class QuestionPart extends StatelessWidget {  final String question;  final String logo;  final int number;  const QuestionPart({    super.key,    required this.question,    required this.number,    required this.logo,  });  @override  Widget build(BuildContext context) {    return Stack(      children: [        Container(          color: const Color(0xffa76ae4),          height: MediaQuery.sizeOf(context).height * .5,        ),        Positioned(            top: MediaQuery.sizeOf(context).height * .2,            right: 0,            child: Image.asset("Assets/images/EllipseSlide.png")),        Positioned(            top: 0,            left: MediaQuery.sizeOf(context).width * .25,            child: Image.asset("Assets/images/top.png")),        Positioned(            top: MediaQuery.sizeOf(context).height * .05,            right: MediaQuery.sizeOf(context).height * .1,            child: Image.asset("Assets/images/top2.png")),        Positioned(          top: MediaQuery.sizeOf(context).height * .05,          left: 0,          child: Hero(            tag: "Logo",            transitionOnUserGestures: true,            child: Image.asset(logo),          ),        ),        Positioned(          bottom: MediaQuery.sizeOf(context).height * .05,          left: MediaQuery.sizeOf(context).width * .1,          right: MediaQuery.sizeOf(context).width * .1,          child: Hero(            tag: "Questions",            transitionOnUserGestures: true,            child: Container(              clipBehavior: Clip.antiAliasWithSaveLayer,              width: MediaQuery.sizeOf(context).width * .8,              decoration: BoxDecoration(                borderRadius: BorderRadius.circular(18),                color: Colors.white,              ),              child: Column(                mainAxisAlignment: MainAxisAlignment.center,                children: [                  Text(                    "Question ($number)",                    style: const TextStyle(                      fontSize: 35,                      fontWeight: FontWeight.w500,                      color: Color(0xff5B1CAE),                    ),                  ),                  SizedBox(                      width: MediaQuery.sizeOf(context).width * .7,                      child: Text(                        question,                        style: const TextStyle(                          fontSize: 25,                          fontWeight: FontWeight.w500,                          color: Color(                            0xff58476e,                          ),                        ),                        textAlign: TextAlign.center,                      )),                ],              ),            ),          ),        ),      ],    );  }}